import React,{useEffect, useState} from 'react'
import {Form, FormGroup, Input, Container, Button} from 'reactstrap'
import axios, { post } from 'axios'
import api from '../api/api.js'

class Compiler extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            codeFile: null,
            outputFile: null,
            inputFile: null,
            timeLimit: 8,
            storageLimit: 80,
        }
        this.handleForm = this.handleForm.bind(this)
        this.onChange = this.onChange.bind(this)
        this.postDataToServer = this.postDataToServer.bind(this)
    }

    componentDidMount() {
        document.title = "Compiler"
    }

    onChange(e) {
        this.setState({
            codeFile: e.target.files[0], 
            outputFile: e.target.files[1],
            inputFile: e.target.files[2],
        })
    }

    handleForm(e) {
        console.log(compiler)
        postDataToServer(compiler)
        e.preventDefault()
    }

    postDataToServer(compiler) {
        const language = document.getElementById("language")
        axios.post(`${api}/${language}`,compiler).then(
            (response) =>  {
                console.log(response)
                console.log("compiler api called")
            },(error) => {
                console.log(error)
                console.log("error")
            }
        )
    }

render() {
    return(
        <div>
            <h1 className="text-center">Compiler</h1>
            <Form onSubmit={this.handleForm}>
            <FormGroup>
                <label>Select Programming Language</label>
                <br/>
                <Input type="select" id="language" onChange={this.onChange}>
                    <option>c</option>
                    <option>cpp</option>
                    <option>java</option>
                    <option>python</option>
                </Input>
            </FormGroup>

            <FormGroup>
                <label>Upload Code File</label>
                <Input type="file" onChange={this.onChange}></Input>
            </FormGroup>

            <FormGroup>
                <label>Upload Expected Output File</label>
                <Input type="file" onChange={this.onChange}></Input>
            </FormGroup>

            <FormGroup>
                <label>Upload Input File</label>
                <Input type="file" onChange={this.onChange}></Input>
            </FormGroup>

            <Container className="text-center">
                    <Button type="submit" color="success">Compile</Button>
            </Container>

            </Form>
            <h3>Output</h3>
            <p></p>
        </div>
    )
}
}

export default Compiler
